import { Component, ElementRef, AfterViewInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})

export class AppComponent {
  title = 'app';

  divElement: ElementRef;

  constructor(private elementRef: ElementRef) { }

  ngAfterViewInit(): void {
    this.divElement = this.elementRef.nativeElement.querySelector('.sua-div');

    if (this.divElement.complete) {
      this.executarComDelay();
    } else {
      this.divElement.addEventListener('load', () => {
        this.executarComDelay();
      });
    }
  }

  executarComDelay(): void {
    setTimeout(() => {
      console.log('Este código será executado após o atraso e o carregamento da div.');
    }, 2000);
  }
}
<div class="d-flex justify-content-center align-items-center h-100">
  <div id="loading-screen" class="loading-screen">
    <img src="../assets/images/Logo-QuickyTest.png" class="img-fluid" />
  </div>
</div>
