import { Component, Inject } from '@angular/core';
import { PromptModel } from '../sub-components/prompt-model/prompt-model.component';
import { HttpClient, HttpHeaders } from '@angular/common/http'
import { environment } from '../../environments/environment';

@Component({
  selector: 'app-quicky-test',
  templateUrl: './quicky-test.component.html',
  styleUrls: ['./quicky-test.component.css']
})
export class QuickyTestComponent {
  model: ProofModel = new ProofModel();
  private apiUrl = environment.apiUrl;
  http: HttpClient;
  respostaProva: string = "";

  qtdConfirm: number = 0;
  qtdNotConfirm: number = 0;
  showOffCanvas: boolean = false;
  generating: boolean = false;

  constructor(http: HttpClient) {
    this.http = http;
  }

  receberRequisicao(value: any): void {
    console.log(value);
  }
  tratamentoDeProva(prova: any) {
    console.log(prova)
    this.respostaProva += prova.content;
  }

  toggleOffCanvas(): void {
    this.showOffCanvas = !this.showOffCanvas;
  }
  addProve(): void  {
    let prompt = new PromptModel(this.model.Prompts.length);
    this.model.Prompts.push(prompt);
    this.verificarStatus();
  }
  verProvas(): void {
    this.generating = true;
    const headers = new HttpHeaders().set('Content-Type', 'application/json');
    this.http.post<ResponseProof>(this.apiUrl + 'proof/Generating', this.model, { headers })
      .toPromise()
      .then(response => {
        this.tratamentoDeProva(response)
      })
      .catch(error => {
        console.log(error)
      })

  }
  verificarStatus() {
    this.qtdConfirm = this.model.Prompts.filter(x => x.confirmado === true).length;
    this.qtdNotConfirm = this.model.Prompts.filter(x => x.confirmado === false).length;
  }

  receberAviso() {
    this.verificarStatus();
  }
  deleteProof(id: number) {
    let index: number = this.model.Prompts.findIndex(prompt => prompt.id === id);
    this.model.Prompts.splice(index, 1);
    this.verificarStatus();
  }
}

export class ProofModel {
  API_KEY: string;
  VisualReturn: boolean;
  Prompts: PromptModel[];

  constructor() {
    this.API_KEY = "";
    this.VisualReturn = true;
    this.Prompts = [];
  }
}

export class ResponseProof {
  pdfURL: string;
  wordURL: string;
  content: string;
  status: string;
  uuid_usuario: string;
  uuid_prova: string;

  constructor() {
    this.pdfURL = "";
    this.wordURL = "";
    this.content = "";
    this.status = "";
    this.uuid_usuario = "";
    this.uuid_prova = "";
  }
}
<div id="content" class="container-fluid d-flex justify-content-center align-items-center" style="height: 100vh;">
  <div class="container-fluid">
    <div class="row fixed-top mt-4 mx-2">
      <div class="col d-flex justify-content-center">
        <app-visual-proof [texto]="respostaProva" [gerando]="generating"></app-visual-proof>
      </div>
    </div>
  </div>
  <div (click)="toggleOffCanvas()" id="chat-circle" class="btn btn-raised">
    <div id="chat-overlay"></div>
    <i class="material-icons"><img src="../../assets/images/conversation.png" class="img-fluid" /></i>
  </div>
  <div class="offcanvas offcanvas-end" [class]="{'show': showOffCanvas}" data-bs-scroll="true" tabindex="-1" id="offcanvasWithBothOptions" aria-labelledby="offcanvasWithBothOptionsLabel">
    <div class="offcanvas-header">
      <div class="col">
        <input [(ngModel)]="model.API_KEY" class="form-control" type="text" placeholder="Chave API" aria-label="Chave API">
      </div>
      <div class="mx-2">
        <button (click)="toggleOffCanvas()" type="button" class="btn-close" data-bs-dismiss="offcanvas" aria-label="Close"></button>
      </div>
    </div>
    <div class="offcanvas-header">
      <div class="col">
        <button class="btn btn-info position-relative" (click)="addProve()">
          Adicionar Prova <span class="badge text-bg-success">{{ qtdConfirm }}</span>&nbsp;<span class="badge text-bg-danger">{{ qtdNotConfirm }}</span>
        </button>
      </div>
      <div class="col-2 d-flex align-items-center justify-content-end">
        <button class="btn btn-success" (click)="verProvas()">Enviar</button>
      </div>
    </div>
    <div class="offcanvas-header">
      <div class="form-check form-switch">
        <input [(ngModel)]="model.VisualReturn" class="form-check-input" type="checkbox" id="flexSwitchCheckChecked">
        <label class="form-check-label" for="flexSwitchCheckChecked">Retorno Visual&nbsp;<small>( Apenas uma prova )</small></label>
      </div>
    </div>
    <div class="offcanvas-body">
      <div class="row">
        <div class="container-fluid">
          <app-prompt-model *ngFor="let prompt of model.Prompts" [prompt]="prompt" (delete)="deleteProof($event)" (status)="receberAviso()"></app-prompt-model>
        </div>
      </div>
    </div>
  </div>
</div>
