import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})
export class AppComponent {
  title = 'app';

  content: HTMLElement;
  loadingScreen: HTMLElement;

  constructor() {
    this.content = document.getElementById('content') as HTMLElement;
    this.loadingScreen = document.getElementById('loading-screen') as HTMLElement;
  }

  removeLoadingScreen(): void {
    this.loadingScreen.classList.add('finalizado');
    setInterval(() => {
      this.loadingScreen.style.display = 'none';
      this.content.style.display = 'block';
    }, 1900);
  }

  ngOnInit(): void {
    document.addEventListener('DOMContentLoaded', () => {
      this.removeLoadingScreen();
    });
  }
}
<div class="d-flex justify-content-center align-items-center h-100">
  <div id="loading-screen" class="loading-screen">
    <img src="../assets/images/Logo-QuickyTest.png" class="img-fluid" />
  </div>
</div>
