import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-prompt-model',
  templateUrl: './prompt-model.component.html',
  styleUrls: ['./prompt-model.component.css']
})

export class PromptModelComponent {
  @Input() prompt!: PromptModel;

  questions: number = 10;

  changeAssunto(): void {

  }
  changeMateria(): void {

  }
  changeSerie(): void {

  }
  changeQtdQuestoes(event: any): void {
    this.questions = event.target.value;
  }
  changePossuiContexto(): void {

  }
}

export class PromptModel {

  assunto: string;
  materia: string;
  serie: string;
  nivel: string;
  qtdquestoes: number;
  possuicontexto: boolean;

  constructor()
  {
    this.assunto = "";
    this.materia = "";
    this.serie = "";
    this.nivel = "";
    this.qtdquestoes = 10;
    this.possuicontexto = false;
  }
}
<div class="accordion" id="accordionPanelsStayOpenExample">
  <div class="accordion-item">
    <h2 class="accordion-header">
      <button class="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#panelsStayOpen-collapseOne" aria-expanded="false" aria-controls="panelsStayOpen-collapseOne">
        {{prompt.materia}} - {{prompt.assunto}}
      </button>
    </h2>
    <div id="panelsStayOpen-collapseOne" class="accordion-collapse collapse">
      <div class="accordion-body">
        <input class="form-control" value="{{prompt.materia}}" type="text" placeholder="Materia" aria-label="Materia">
        <input class="form-control" value="{{prompt.assunto}}" type="text" placeholder="Assunto" aria-label="Assunto">
        <input class="form-control" value="{{prompt.nivel}}" type="text" placeholder="Nivel" aria-label="Nivel">
        <input class="form-control" value="{{prompt.serie}}" type="text" placeholder="Serie" aria-label="Serie">
        <label for="qtd" class="form-label">Quantidade de questoes: {{ questions }}</label>
        <input type="range" class="form-range" value="{{prompt.qtdquestoes}}" min="1" max="10" id="qtd" (input)="changeQtdQuestoes($event)">
        <div class="form-check">
          <input class="form-check-input" type="checkbox" [(ngModel)]="prompt.possuicontexto" value="" id="flexCheckDefault">
          <label class="form-check-label" for="flexCheckDefault">Contexto</label>
        </div>
      </div>
    </div>
  </div>
</div>
