import { Component, ElementRef, AfterViewInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})

export class AppComponent {
  title = 'app';

  exibirAnimacao = false;

  constructor() { }

  ngOnInit() {
    setTimeout(() => {
      this.exibirAnimacao = true;
    }, 1900);
  }
}
<div [ngClass]="{'finalizado': exibirAnimacao}" class="d-flex justify-content-center align-items-center h-100">
  <div id="loading-screen" class="loading-screen">
    <img src="../assets/images/Logo-QuickyTest.png" class="img-fluid" />
  </div>
</div>

<app-quicky-test></app-quicky-test>
