import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-prompt-model',
  templateUrl: './prompt-model.component.html',
  styleUrls: ['./prompt-model.component.css']
})

export class PromptModelComponent {
  public confirmed: boolean = false;

  @Input() prompt!: PromptModel;

  @Output() valueChanged: EventEmitter<PromptModel> = new EventEmitter<PromptModel>();

  emitValue() {
    this.valueChanged.emit(this.prompt);
    this.confirmed = true;
  }

  changeAssunto(event: any): void {
    this.prompt.assunto = event.target.value;
  }
  changeMateria(event: any): void {
    this.prompt.materia = event.target.value;
  }
  changeSerie(event: any): void {
    this.prompt.serie = event.target.value;
  }
  changeNivel(event: any): void {
    this.prompt.nivel = event.target.value;
  }
  changeQtdQuestoes(event: any): void {
    this.prompt.qtdquestoes = event.target.value;
  }
  changePossuiContexto(event: any): void {
    this.prompt.possuicontexto = event.checked;
  }
}

export class PromptModel {

  assunto: string;
  materia: string;
  serie: string;
  nivel: string;
  qtdquestoes: number;
  possuicontexto: boolean;

  constructor()
  {
    this.assunto = "";
    this.materia = "";
    this.serie = "";
    this.nivel = "";
    this.qtdquestoes = 10;
    this.possuicontexto = false;
  }
}
<div class="accordion" id="accordionPanelsStayOpenExample">
  <div class="accordion-item">
    <h2 class="accordion-header">
      <button class="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#panelsStayOpen-collapseOne" aria-expanded="false" aria-controls="panelsStayOpen-collapseOne">
        {{prompt.materia}} - {{prompt.assunto}} | Status: <strong [ngStyle]="{'color': confirmed ? 'green' : 'red' }">{{ confirmed ? 'Confirmado' : 'Não Confirmado' }}</strong>
      </button>
    </h2>
    <div id="panelsStayOpen-collapseOne" class="accordion-collapse collapse">
      <div class="accordion-body">
        <input class="form-control" value="{{prompt.materia}}" type="text" placeholder="Materia" aria-label="Materia" (input)="changeMateria($event)">
        <input class="form-control" value="{{prompt.assunto}}" type="text" placeholder="Assunto" aria-label="Assunto" (input)="changeAssunto($event)">
        <input class="form-control" value="{{prompt.nivel}}" type="text" placeholder="Nivel" aria-label="Nivel" (input)="changeNivel($event)">
        <input class="form-control" value="{{prompt.serie}}" type="text" placeholder="Serie" aria-label="Serie" (input)="changeSerie($event)">
        <label for="qtd" class="form-label">Quantidade de questoes: {{prompt.qtdquestoes}}</label>
        <input type="range" class="form-range" value="{{prompt.qtdquestoes}}" min="1" max="10" id="qtd" (input)="changeQtdQuestoes($event)">
        <div class="form-check">
          <input class="form-check-input" type="checkbox" [(ngModel)]="prompt.possuicontexto" value="" id="flexCheckDefault" (input)="changePossuiContexto($event)">
          <label class="form-check-label" for="flexCheckDefault">Contexto</label>
        </div>
        <button type="button" class="btn btn-primary" (click)="emitValue()" [ngStyle]="{'display': confirmed ? 'none' : 'block'}">Confirmar Prova</button>
      </div>
    </div>
  </div>
</div>
