import { Component } from '@angular/core';
import { PromptModel } from '../sub-components/prompt-model/prompt-model.component';
import { HttpClient } from '@angular/common/http'

@Component({
  selector: 'app-quicky-test',
  templateUrl: './quicky-test.component.html',
  styleUrls: ['./quicky-test.component.css']
})
export class QuickyTestComponent {
  proofModel: ProofModel = new ProofModel();
  qtdConfirm: number = 0;
  qtdNotConfirm: number = 0;

  addProve() {
    let prompt = new PromptModel();
    this.proofModel.Prompts.push(prompt);
    this.verificarStatus();
  }
  verProvas(): void {
    console.log(this.proofModel.Prompts);
  }
  verificarStatus() {
    this.qtdConfirm = this.proofModel.Prompts.filter(x => x.confirmado === true).length;
    this.qtdNotConfirm = this.proofModel.Prompts.filter(x => x.confirmado === false).length;
  }



  receberAviso() {
    this.verificarStatus();
  }
}

export class ProofModel {
  API_KEY: string;
  VisualReturn: boolean;
  Prompts: PromptModel[];

  constructor() {
    this.API_KEY = "";
    this.VisualReturn = true;
    this.Prompts = [];
  }
}
<div id="content" class="container-fluid d-flex justify-content-center align-items-center" style="height: 100vh;">
  <div id="center-text">
    <h1>Quicky Test</h1>
    <p>Message send and scroll to bottom enabled</p>
  </div>
  <div id="body">
    <div id="chat-circle" class="btn btn-raised">
      <div id="chat-overlay"></div>
      <i class="material-icons"><img src="../../assets/images/conversation.png" class="img-fluid" /></i>
    </div>
    <div class="chat-box">
      <div class="chat-box-header">
        <span class="chat-box-toggle">Fechar</span>
      </div>
      <div class="chat-box-body">
        <div class="chat-box-overlay">
        </div>
        <div class="chat-logs">

        </div>
      </div>
      <div class="chat-input">
        <div class="container-fluid d-flex mt-2">
          <div class="row">
            <div class="col-3">
              <button class="btn btn-info position-relative" (click)="addProve()">
                Adicionar Prova <span class="badge text-bg-success">{{ qtdConfirm }}</span>&nbsp;<span class="badge text-bg-danger">{{ qtdNotConfirm }}</span>
              </button>
            </div>
            <div class="col-3">
              <div class="d-flex align-items-center">
                <div class="form-check form-switch">
                  <input class="form-check-input" type="checkbox" id="flexSwitchCheckChecked" checked>
                  <label class="form-check-label" for="flexSwitchCheckChecked">Retorno Visual&nbsp;<small>( Apenas uma prova )</small></label>
                </div>
              </div>
            </div>
            <div class="col-3">
              <div>
                <button class="btn btn-success" (click)="verProvas()">Enviar</button>
              </div>
            </div>
          </div>
        </div>
        <div class="container-fluid mt-2 list-of-proofs">
          <app-prompt-model *ngFor="let prompt of proofModel.Prompts" [prompt]="prompt" (status)="receberAviso()"></app-prompt-model>
        </div>
      </div>
    </div>
  </div>
</div>
